import { useWallet } from "@txnlab/use-wallet-react";
import { ExternalLink, Globe, Wallet } from "lucide-react";
import { useMemo } from "react";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent } from "@/components/ui/card";
import { ellipseAddress } from "../utils/ellipseAddress";
import { getAlgodConfigFromEnvironment } from "../utils/network/getAlgoClientConfigs";

const Account = () => {
  const { activeAddress } = useWallet();
  const algoConfig = getAlgodConfigFromEnvironment();

  const networkName = useMemo(() => {
    return algoConfig.network === ""
      ? "localnet"
      : algoConfig.network.toLocaleLowerCase();
  }, [algoConfig.network]);

  const networkColor = useMemo(() => {
    switch (networkName) {
      case "mainnet":
        return "bg-green-100 text-green-800 border-green-200";
      case "testnet":
        return "bg-blue-100 text-blue-800 border-blue-200";
      case "localnet":
        return "bg-orange-100 text-orange-800 border-orange-200";
      default:
        return "bg-gray-100 text-gray-800 border-gray-200";
    }
  }, [networkName]);

  return (
    <Card className="bg-gradient-to-r from-blue-50 to-purple-50 border-blue-200">
      <CardContent className="p-4 space-y-3">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2">
            <Wallet className="w-4 h-4 text-blue-600" />
            <span className="text-sm font-medium text-gray-700">
              Connected Account
            </span>
          </div>
          <Badge className={`${networkColor} font-medium`}>
            <Globe className="w-3 h-3 mr-1" />
            {networkName}
          </Badge>
        </div>

        <div className="flex items-center gap-2">
          <a
            className="text-blue-600 hover:text-blue-800 font-mono text-sm flex items-center gap-1 hover:underline transition-colors"
            href={`https://lora.algokit.io/${networkName}/account/${activeAddress}/`}
            target="_blank"
            rel="noopener noreferrer"
          >
            {ellipseAddress(activeAddress)}
            <ExternalLink className="w-3 h-3" />
          </a>
        </div>
      </CardContent>
    </Card>
  );
};

export default Account;

"use client";
import { AlgorandClient } from "@algorandfoundation/algokit-utils";
import {
  OnSchemaBreak,
  OnUpdate,
} from "@algorandfoundation/algokit-utils/types/app";
import { useWallet } from "@txnlab/use-wallet-react";
import { Code, Loader2, Sparkles } from "lucide-react";
import { useSnackbar } from "notistack";
import { useState } from "react";
import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { HelloWorldFactory } from "../contracts/hello_world";
import {
  getAlgodConfigFromEnvironment,
  getIndexerConfigFromEnvironment,
} from "../utils/network/getAlgoClientConfigs";

interface AppCallsInterface {
  openModal: boolean;
  setModalState: (value: boolean) => void;
}

const AppCalls = ({ openModal, setModalState }: AppCallsInterface) => {
  const [loading, setLoading] = useState<boolean>(false);
  const [contractInput, setContractInput] = useState<string>("");
  const { enqueueSnackbar } = useSnackbar();
  const { transactionSigner, activeAddress } = useWallet();

  const algodConfig = getAlgodConfigFromEnvironment();
  const indexerConfig = getIndexerConfigFromEnvironment();
  const algorand = AlgorandClient.fromConfig({
    algodConfig,
    indexerConfig,
  });
  algorand.setDefaultSigner(transactionSigner);

  const sendAppCall = async () => {
    setLoading(true);

    // Please note, in typical production scenarios,
    // you wouldn't want to use deploy directly from your frontend.
    // Instead, you would deploy your contract on your backend and reference it by id.
    // Given the simplicity of the starter contract, we are deploying it on the frontend
    // for demonstration purposes.
    const factory = new HelloWorldFactory({
      defaultSender: activeAddress ?? undefined,
      algorand,
    });
    const deployResult = await factory
      .deploy({
        onSchemaBreak: OnSchemaBreak.AppendApp,
        onUpdate: OnUpdate.AppendApp,
      })
      .catch((error: Error) => {
        console.error("Contract deployment failed:", error);
        enqueueSnackbar(`Error deploying the contract: ${error.message}`, {
          variant: "error",
        });
        setLoading(false);
        return;
      });

    if (!deployResult) {
      return;
    }

    const { appClient } = deployResult;

    const response = await appClient.send
      .hello({ args: { name: contractInput } })
      .catch((error: Error) => {
        console.error("Contract call failed:", error);
        enqueueSnackbar(`Error calling the contract: ${error.message}`, {
          variant: "error",
        });
        setLoading(false);
        return;
      });

    if (!response) {
      return;
    }

    enqueueSnackbar(`Response from the contract: ${response.return}`, {
      variant: "success",
    });
    setContractInput("");
    setLoading(false);
  };

  return (
    <Dialog open={openModal} onOpenChange={setModalState}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Code className="w-5 h-5" />
            Smart Contract Interaction
          </DialogTitle>
          <DialogDescription>
            Say hello to your Algorand smart contract! This will deploy and
            interact with a simple contract.
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="contract-input">Message for Hello Function</Label>
            <Input
              id="contract-input"
              placeholder="Enter your name or message"
              value={contractInput}
              onChange={(e) => setContractInput(e.target.value)}
              className="border-gray-200 focus:border-purple-500"
            />
          </div>

          <div className="bg-blue-50 border border-blue-200 rounded-lg p-3">
            <p className="text-sm text-blue-800">
              <strong>Note:</strong> This will deploy a new contract instance
              for demonstration purposes. In production, contracts are typically
              deployed once and referenced by ID.
            </p>
          </div>

          <div className="flex gap-2 pt-4">
            <Button
              variant="outline"
              className="flex-1"
              onClick={() => setModalState(false)}
            >
              Close
            </Button>
            <Button
              className="flex-1 bg-purple-600 hover:bg-purple-700"
              disabled={!contractInput.trim() || loading}
              onClick={sendAppCall}
            >
              {loading ? (
                <>
                  <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                  Processing...
                </>
              ) : (
                <>
                  <Sparkles className="w-4 h-4 mr-2" />
                  Send Contract Call
                </>
              )}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
};

export default AppCalls;

import { useWallet, type Wallet, WalletId } from "@txnlab/use-wallet-react";
import { LogOut, Wallet as WalletIcon } from "lucide-react";
import Image from "next/image";
import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Separator } from "@/components/ui/separator";
import Account from "./Account";

interface ConnectWalletInterface {
  openModal: boolean;
  closeModal: () => void;
}

const ConnectWallet = ({ openModal, closeModal }: ConnectWalletInterface) => {
  const { wallets, activeAddress } = useWallet();

  const isKmd = (wallet: Wallet) => wallet.id === WalletId.KMD;

  return (
    <Dialog open={openModal} onOpenChange={closeModal}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <WalletIcon className="w-5 h-5" />
            Select Wallet Provider
          </DialogTitle>
          <DialogDescription>
            Choose your preferred wallet to connect to the Algorand network.
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4">
          {activeAddress && (
            <div className="space-y-4">
              <Account />
              <Separator />
            </div>
          )}

          {!activeAddress && (
            <div className="space-y-2">
              {wallets?.map((wallet) => (
                <Button
                  key={`provider-${wallet.id}`}
                  variant="outline"
                  className="w-full justify-start h-12 hover:bg-blue-50 border-gray-200 hover:border-blue-300"
                  data-test-id={`${wallet.id}-connect`}
                  onClick={() => wallet.connect()}
                >
                  {!isKmd(wallet) && (
                    <Image
                      alt={`${wallet.metadata.name} icon`}
                      src={wallet.metadata.icon}
                      width={24}
                      height={24}
                      className="mr-3 object-contain"
                    />
                  )}
                  {isKmd(wallet) && <WalletIcon className="w-6 h-6 mr-3" />}
                  <span className="font-medium">
                    {isKmd(wallet) ? "LocalNet Wallet" : wallet.metadata.name}
                  </span>
                </Button>
              ))}
            </div>
          )}

          <div className="flex gap-2 pt-4">
            <Button
              variant="outline"
              className="flex-1"
              data-test-id="close-wallet-modal"
              onClick={closeModal}
            >
              Close
            </Button>
            {activeAddress && (
              <Button
                variant="destructive"
                className="flex-1"
                data-test-id="logout"
                onClick={async () => {
                  if (wallets) {
                    const activeWallet = wallets.find((w) => w.isActive);
                    if (activeWallet) {
                      await activeWallet.disconnect();
                    } else {
                      // Required for logout/cleanup of inactive providers
                      localStorage.removeItem("@txnlab/use-wallet:v3");
                      window.location.reload();
                    }
                  }
                }}
              >
                <LogOut className="w-4 h-4 mr-2" />
                Logout
              </Button>
            )}
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
};
export default ConnectWallet;

"use client";

import React, { type ReactNode } from "react";

interface ErrorBoundaryProps {
  children: ReactNode;
}

interface ErrorBoundaryState {
  hasError: boolean;
  error: Error | null;
}

class ErrorBoundary extends React.Component<
  ErrorBoundaryProps,
  ErrorBoundaryState
> {
  constructor(props: ErrorBoundaryProps) {
    super(props);
    this.state = { hasError: false, error: null };
  }

  static getDerivedStateFromError(error: Error): ErrorBoundaryState {
    // Update state so the next render will show the fallback UI.
    return { hasError: true, error };
  }

  render(): ReactNode {
    if (this.state.hasError) {
      // You can render any custom fallback UI
      return (
        <div className="hero min-h-screen bg-teal-400">
          <div className="hero-content mx-auto max-w-md rounded-lg bg-white p-6 text-center">
            <div className="max-w-md">
              <h1 className="text-4xl">Error occured</h1>
              <p className="py-6">
                {this.state.error?.message.includes(
                  "Attempt to get default algod configuration",
                )
                  ? "Please make sure to set up your environment variables correctly. Create a .env file based on .env.template and fill in the required values. This controls the network and credentials for connections with Algod and Indexer."
                  : this.state.error?.message}
              </p>
            </div>
          </div>
        </div>
      );
    }

    return this.props.children;
  }
}

export default ErrorBoundary;

