import { Loader2, Send } from "lucide-react";
import { useSnackbar } from "notistack";
import { useState } from "react";
import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { getAlgodConfigFromEnvironment } from "../utils/network/getAlgoClientConfigs";

interface TransactInterface {
  openModal: boolean;
  setModalState: (value: boolean) => void;
}

const Transact = ({ openModal, setModalState }: TransactInterface) => {
  const [loading, setLoading] = useState<boolean>(false);
  const [receiverAddress, setReceiverAddress] = useState<string>("");

  const algodConfig = getAlgodConfigFromEnvironment();
  const algorand = AlgorandClient.fromConfig({ algodConfig });

  const { enqueueSnackbar } = useSnackbar();
  const { transactionSigner, activeAddress } = useWallet();

  const handleSubmitAlgo = async () => {
    setLoading(true);

    if (!activeAddress) {
      enqueueSnackbar("Please connect wallet first", { variant: "warning" });
      setLoading(false);
      return;
    }

    try {
      enqueueSnackbar("Sending transaction...", { variant: "info" });
      const result = await algorand.send.payment({
        signer: transactionSigner,
        sender: activeAddress,
        receiver: receiverAddress,
        amount: algo(1),
      });
      enqueueSnackbar(`Transaction sent: ${result.txIds[0]}`, {
        variant: "success",
      });
      setReceiverAddress("");
      setModalState(false);
    } catch (error) {
      console.error("Transaction failed:", error);
      enqueueSnackbar("Failed to send transaction", { variant: "error" });
    }

    setLoading(false);
  };

  const isValidAddress = receiverAddress.length === 58;

  return (
    <Dialog open={openModal} onOpenChange={setModalState}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Send className="w-5 h-5" />
            Send Payment Transaction
          </DialogTitle>
          <DialogDescription>
            Send 1 ALGO to another wallet address on the Algorand network.
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="receiver-address">Recipient Address</Label>
            <Input
              id="receiver-address"
              data-test-id="receiver-address"
              placeholder="Enter 58-character wallet address"
              value={receiverAddress}
              onChange={(e) => setReceiverAddress(e.target.value)}
              className={`${
                receiverAddress && !isValidAddress
                  ? "border-red-300 focus:border-red-500"
                  : "border-gray-200 focus:border-blue-500"
              }`}
            />
            {receiverAddress && !isValidAddress && (
              <p className="text-sm text-red-600">
                Address must be exactly 58 characters long
              </p>
            )}
          </div>

          <div className="flex gap-2 pt-4">
            <Button
              variant="outline"
              className="flex-1"
              onClick={() => setModalState(false)}
            >
              Cancel
            </Button>
            <Button
              className="flex-1 bg-green-600 hover:bg-green-700"
              data-test-id="send-algo"
              disabled={!isValidAddress || loading}
              onClick={handleSubmitAlgo}
            >
              {loading ? (
                <>
                  <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                  Sending...
                </>
              ) : (
                <>
                  <Send className="w-4 h-4 mr-2" />
                  Send 1 ALGO
                </>
              )}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
};

export default Transact;

import * as React from "react";

const MOBILE_BREAKPOINT = 768;

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(
    undefined,
  );

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`);
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT);
    };
    mql.addEventListener("change", onChange);
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT);
    return () => mql.removeEventListener("change", onChange);
  }, []);

  return !!isMobile;
}

